mapper.update:
UPDATE application SET
capacity = #{capacity},
model = #{model},
address = #{address}
where id = ${id}

mapper.delete:
DELETE FROM ApplicationPhotos

	id IN (
		SELECT id FROM ApplicationPhotos
		WHERE
		 	applicationId IN

				${elem.applicationId}	

			and 
			photoId IN

				${elem.photoId}	

	)

mapper.select:
SELECT p.* FROM ApplicationPhotos a LEFT JOIN PhotoInfo p on (a.photoId = p.id)
a.applicationId = ${id}
ORDER BY p.id
